select u.id,u.parentid,u.agent,u.name,u.code,u.password,u.mobilephone,u.email,u.ownindustry, u.orgname,u.usertype,u.address,u.note,u.fileaddress,u.companyfullname,u.companyshortname,u.province,u.city, u.companycode,u.status,u.endtime,u.maxLanMuNumber,u.maxKeyWordNumber, u.beizhu, u.authority, u.subjecttimes,u.microopen,u.monitortimes,u.warntimes,u.persontimes,u.expirydate,u.cloudtimes,u.childtimes,u.usertimes,u.registertime,u.managerid,u.startdate,u.typecode, m.name as operateruser from bs_user as u left join bs_manageruser as m on u.managerid = m.id where 1=1
and u.name LIKE CONCAT(CONCAT('%', #{name}), '%')
and u.mobilephone LIKE CONCAT(CONCAT('%', #{mobilephone}), '%')
and u.orgname LIKE CONCAT(CONCAT('%', #{orgname}), '%')
and u.usertype = #{usertype,jdbcType=VARCHAR}
and u.typecode = #{typecode,jdbcType=VARCHAR}
and u.status = #{status,jdbcType=INTEGER}
and u.status !='5'
and (u.parentid is null or u.parentid ='')
order by u.registertime desc

select u.id,u.parentid,u.agent,u.name,u.code,u.password,u.mobilephone,u.email,u.ownindustry, u.orgname,u.usertype,u.address,u.note,u.fileaddress,u.companyfullname,u.companyshortname,u.province,u.city, u.companycode,u.status,u.endtime,u.maxLanMuNumber,u.maxKeyWordNumber, u.beizhu, u.authority, u.subjecttimes,u.microopen,u.monitortimes,u.warntimes,u.persontimes,u.expirydate,u.cloudtimes,u.childtimes,u.usertimes,u.registertime,u.managerid,u.startdate,u.typecode, m.name as operateruser from bs_user as u left join bs_manageruser as m on u.managerid = m.id
where 1=1 and managerid!=''
and u.name LIKE CONCAT(CONCAT('%', #{name}), '%')
and u.mobilephone LIKE CONCAT(CONCAT('%', #{mobilephone}), '%')
and u.orgname LIKE CONCAT(CONCAT('%', #{orgname}), '%')
and u.usertype = #{usertype,jdbcType=VARCHAR}
and u.typecode = #{typecode,jdbcType=VARCHAR}
and u.status = #{status,jdbcType=INTEGER}
order by u.registertime desc

select u.id,u.parentid,u.agent,u.name,u.code,u.password,u.mobilephone,u.email,u.ownindustry, u.orgname,u.usertype,u.address,u.note,u.fileaddress,u.companyfullname,u.companyshortname,u.province,u.city, u.companycode,u.status,u.endtime,u.maxLanMuNumber,u.maxKeyWordNumber, u.beizhu, u.authority, u.subjecttimes,u.microopen,u.monitortimes,u.warntimes,u.persontimes,u.expirydate,u.cloudtimes,u.childtimes,u.usertimes,u.registertime,u.managerid,u.startdate,u.typecode, m.name as operateruser from bs_user as u left join bs_manageruser as m on u.managerid = m.id left join bs_user_type as t on u.typecode = t.typecode where t.typename <> '1'
and u.name LIKE CONCAT(CONCAT('%', #{name}), '%')
and u.mobilephone LIKE CONCAT(CONCAT('%', #{mobilephone}), '%')
and u.orgname LIKE CONCAT(CONCAT('%', #{orgname}), '%')
and u.usertype = #{usertype,jdbcType=VARCHAR}
and u.typecode = #{typecode,jdbcType=VARCHAR}
and u.status = #{status,jdbcType=INTEGER}
and u.status !='5'
and (u.parentid is null or u.parentid ='')
order by u.registertime desc
